import React from "react";

import DataTable from "react-data-table-component";
import memoize from "memoize-one";
import CardTable from "components/Cards/CardTable.js";

const columns = memoize((onToggleEditMode, onRemoveAdmin) => [
  {
    name: "ID",
    selector: "id_member",
    sortable: true,
  },
  {
    name: "Email",
    selector: "email",
    sortable: true,
  },
  {
    name: "Nama",
    selector: "nama_member",
    sortable: true,
  },
  {
    name: "Aksi",
    center: true,
    cell: (row) => (
      <div>
        <div className="mb-3 mb-xl-0 float-left mr-2">
          <button type="button" onClick={() => onToggleEditMode(row)}>
            <i className="fa fa-pencil"></i>
          </button>
        </div>
        <div className="mb-3 mb-xl-0 float-left">
          <button type="button" onClick={() => onRemoveAdmin(row.uid)}>
            <i className="fa fa-trash"></i>
          </button>
        </div>
      </div>
    ),
  },
]);

export default function Users() {
  const onToggleEditMode = (user) => {
    this.setState((state) => ({
      addMode: !state.addMode,
      editMode: !state.editMode,
      user,
    }));
  };

  const onRemove = async (user) => {
    console.log("test");
  };

  const handleClear = () => {
    const { filterText } = this.state;
    if (filterText) {
      this.setState({ filterText: "" });
    }
  };

  const onChange = (event) => {
    const { name, value } = event.target;
    this.setState({ [name]: value });
  };

  const clearForm = () => {
    this.setState({
      editMode: false,
      addMode: false,
      user: null,
      proses: false,
    });
  };

  const btnMode = () => {
    this.setState((state) => ({
      addMode: !state.addMode,
    }));
  };

  return (
    <>
      <div className="flex flex-wrap mt-4">
        <div className="w-full mb-12 px-4">
          <div className="mb-3 float-right col-md-8">
            <button
              type
              className="px-3 py-2 rounded text-white bg-lightBlue-400"
            >
              Tambah
            </button>
          </div>
          <div className="input-group col-md-4 float-left">
            <input
              name="filterText"
              type="text"
              placeholder="Search By Username"
              value={filterText}
              onChange={onChange}
              className="form-control"
            />
            <div className="input-group-append">
              <button type="button" onClick={handleClear}>
                X
              </button>
            </div>
          </div>
          <DataTable
            columns={columns(onToggleEditMode, onRemove)}
            data={filteredItems}
            highlightOnHover
            pagination
          />
        </div>
        <div className="w-full mb-12 px-4">
          <CardTable color="dark" />
        </div>
      </div>
    </>
  );
}
