import React from "react";

import DataTable from "react-data-table-component";
import memoize from "memoize-one";
// import CardTable from "components/Cards/CardTable.js";
import Axios from "axios";
import FormMember from "../modal/memberForm";
import { ToastContainer, toast } from "react-toastify";
import { DateTimeHelper } from "../../helpers";

const columns = memoize((onToggleEditMode, onRemoveAdmin) => [
  {
    name: "ID",
    selector: "id_member",
    sortable: true,
  },
  {
    name: "Email",
    selector: "email",
    sortable: true,
  },
  {
    name: "Nama",
    selector: "nama_member",
    sortable: true,
  },
  {
    name: "Aksi",
    center: true,
    cell: (row) => (
      <div>
        <div
          className="mb-3 mb-xl-0 float-left"
          style={{ marginRight: "2rem" }}
        >
          <button type="button" onClick={() => onToggleEditMode(row)}>
            <i class="fas fa-pencil-alt"></i>
          </button>
        </div>
        <div className="mb-3 mb-xl-0 float-left">
          <button type="button" onClick={() => onRemoveAdmin(row.id)}>
            <i className="fa fa-trash"></i>
          </button>
        </div>
      </div>
    ),
  },
]);

export default function Users() {
  const [search, setSearch] = React.useState("");
  const [handle, setHandle] = React.useState({
    editMode: false,
    addMode: false,
    user: null,
    proses: false,
  });
  const [data, setData] = React.useState([]);
  const filteredItems = data.filter(
    (item) => item.nama_member && item.nama_member.includes(search)
  );

  React.useEffect(() => {
    Axios.get("http://localhost:3001/api/member").then((response) => {
      setData(response.data);
    });
  }, []);

  const onSave = async (formData) => {
    Axios.post("http://localhost:3001/api/member/add", {
      name: formData.name,
      email: formData.email,
      password: formData.password,
      gender: formData.gender,
      place: formData.place,
      address: formData.address,
      date: DateTimeHelper.getFormatedDate(
        formData.date,
        "YYYY-MM-DD HH:mm:ss"
      ),
      phone: formData.phone,
      status: formData.status,
    }).then(() => {
      toast.success("Member berhasil ditambah");
      window.location.reload();
    });
  };

  const onToggleEditMode = (user) => {
    setHandle((prevState) => ({
      ...prevState,
      addMode: true,
      editMode: true,
      user,
    }));
  };

  const onRemove = async (id) => {
    Axios.delete(`http://localhost:3001/api/member/delete/${id}`);
  };

  const handleClear = () => {
    if (search) {
      setSearch("");
    }
  };

  const onChange = (event) => {
    const { value } = event.target;
    setSearch(value);
  };

  const clearForm = () => {
    setHandle({
      editMode: false,
      addMode: false,
      user: null,
      proses: false,
    });
  };

  const btnMode = () => {
    setHandle((prevState) => ({ ...prevState, addMode: !handle.addMode }));
  };

  const onCloseModal = () => {
    setHandle((prevState) => ({ ...prevState, addMode: !handle.addMode }));
    clearForm();
  };

  return (
    <>
      <ToastContainer position="top-center" />
      <div className="flex flex-wrap mt-4">
        <div className="w-full mb-12 p-4" style={{ marginTop: "4rem" }}>
          <div className="relative flex flex-col min-w-0 break-words w-full mb-6 shadow-lg rounded bg-lightBlue-900 text-white"></div>
          <div className="mb-3 float-right w-">
            <button
              type
              className="px-6 py-2 rounded text-white bg-lightBlue-400 font-semibold text-sm"
              onClick={btnMode}
            >
              Tambah
            </button>
          </div>
          <div className="flex float-left items-center mb-8">
            <input
              name="search"
              type="text"
              placeholder="Cari nama"
              value={search}
              onChange={onChange}
              className="w-2/12 border-0 px-3 py-3 placeholder-blueGray-300 text-blueGray-600 bg-white rounded text-sm focus:outline-none focus:ring ease-linear transition-all duration-150"
            />
            <div className="flex" style={{ marginLeft: "-30px" }}>
              <button
                type="button"
                onClick={handleClear}
                className="px-3 py-3 text-white bg-red-400 font-bold"
                style={{
                  paddingBottom: ".6rem",
                  paddingTop: ".6rem",
                  marginLeft: "-2px",
                  borderTopRightRadius: "5px",
                  borderBottomRightRadius: "5px",
                }}
              >
                X
              </button>
            </div>
          </div>
          <DataTable
            columns={columns(onToggleEditMode, onRemove)}
            data={filteredItems}
            highlightOnHover
            pagination
          />
        </div>
        {/* <div className="w-full mb-12 px-4">
          <CardTable color="dark" />
        </div> */}
        <FormMember
          user={handle.user}
          onSave={onSave}
          editMode={handle.editMode}
          onCloseModal={onCloseModal}
          clearForm={clearForm}
          proses={handle.proses}
          open={handle.addMode}
        />
      </div>
    </>
  );
}
